export var token = '';
export var personId = 0;
export var idType = '';
export var userName = '';

export function setToken(_token) {
    token = _token;
}
export function setPersonId(_personId) {
    personId = _personId;
}
export function setIdType(_idName) {
    idType = _idName;
}
export function setUsername(_name) {
    userName = _name;
}

export function clearAll() {
    token = '';
    personId = 0;
    idType = '';
    userName = '';
}
